const { UERS_SIGNIN_REQUSET, UERS_SIGNIN_SUCCESS, UERS_SIGNIN_FAIL } = require("../constants/userContants");

function userSigninReducer(state= {}, action){
    switch(action.type){
        case UERS_SIGNIN_REQUSET:
            return { loading: true};
        case UERS_SIGNIN_SUCCESS:
            return {loading: false, userInfo: action.payload};
        case UERS_SIGNIN_FAIL:
            return { loading: false, error: action.payload};
        default: return state;
    }
}
export { userSigninReducer };
