import React from 'react';
import './App.css';
import data from './data';
import HomeScreen from './Screens/HomeScreen';
import ProductScreen from './Screens/ProductScreen';
import {BrowserRouter, Route, Link} from 'react-router-dom';
function App(){

  const  openMenu = () => {
    document.querySelector(".siderbar").classList.add("open");
  }
  const closeMenu = () => {
    document.querySelector(".siderbar").classList.remove("open");
  }

  return (
    <BrowserRouter>
      <div className="grid-container">
              <header className="header">
                  <div className="brand">
                      <button onClick={openMenu}>
                          &#9776;
                      </button>
                      <Link to="/" >amadone</Link>
                  </div>
                  <div className="header-links">
                      <a href="cart.html">Cart</a>
                      <a href="singin.html">Sign In</a>
                  </div>
              </header>
              <aside className="siderbar">
                  <h3>Shopping Categories</h3>
                  <button className="siderbar-close-button" onClick={closeMenu}>x</button>
                  <ul>
                      <li>
                          <a href="index.html">Pants</a>
                      </li>
                      <li>
                          <a href="index.html">Shirts</a>
                      </li>
                  </ul>
              </aside>

              <main className="main">
                  <div className="content">
                    <Route path="/product/:id" component={ProductScreen} />
                    <Route path="/" exact={true} component={HomeScreen} />

                  </div>

              </main>
              <footer className="footer">
                  <i> All right nDs</i>
              </footer>
          </div>
        </BrowserRouter>
  );
}

export default App;
