import React, { useEffect, useState } from 'react';
//import data from '../data';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { saveProduct } from '../actions/productActions';


function ProductsScreen(props) {

    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState('');
    const [brand, setBrand] = useState('');
    const [category, setCategory] = useState('');
    const [countInStock, setCountInStock] = useState('');
    const [descriptions, setDescription] = useState('');

    const ProductSave = useSelector(state => state.productSave);
    const { loading: loadingSave, success: successSave, error: errorSave } = ProductSave;
    const dispatch = useDispatch();

    useEffect(() => {
      
              return () => {
                  //
          };
  
      }, []);

     const submitHandler = (e) => {
         e.preventDefault();
         dispatch(saveProduct({name, price, image, brand, category, countInStock, descriptions}));
     }
    return <div className="form">
        <form onSubmit= {submitHandler}>
            <ul className="form-container">
                <li>
                    <h2>Create Product</h2>
                </li>
                { loadingSave && <div>Loading...</div>}
                { errorSave && <div> {error} </div>}
                <li>
                    <label htmlFor="name">
                        Name
                    </label>
                    <input type="text" name="name" id="name" onChange={(e) => setName(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="price">
                        Price
                    </label>
                    <input type="text" name="price" id="price" onChange={(e) => setPrice(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="image">
                        Image
                    </label>
                    <input type="text" name="image" id="image" onChange={(e) => setImage(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="brand">
                        Brand
                    </label>
                    <input type="text" name="brand" id="brand" onChange={(e) => setBrand(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="categorye">
                        Category
                    </label>
                    <input type="text" name="category" id="category" onChange={(e) => setCategory(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="countInStock">
                        Count-InStock
                    </label>
                    <input type="text" name="countInStock" id="countInStock" onChange={(e) => setCountInStock(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="descriptions">
                        Description
                    </label>
                    <textarea name="descriptions" id="descriptions" onChange={(e) => setDescription(e.target.value)}></textarea>
                </li>
                <li>
                    <button type="submit" className="button signin">Create</button>
                </li>
                <li>
                    New to amadone?
                </li>
                <li>
                    <Link to="/register" className="button secondary" >Create your amadone account</Link>
                </li>
            </ul>
        </form>
       
    </div>
        
}
export default ProductsScreen;
