import React, { useEffect, useState } from 'react';
//import data from '../data';
import { Link } from 'react-router-dom';
import { useSelector, useDispatch } from 'react-redux';
import { saveProduct } from '../actions/productActions';


function ProductsScreen(props) {

    const [name, setName] = useState('');
    const [price, setPrice] = useState('');
    const [image, setImage] = useState('');
    const [brand, setBrand] = useState('');
    const [category, setCategory] = useState('');
    const [countInStock, setCountInStock] = useState('');
    const [descriptions, setDescription] = useState('');
    const [rating, setRating] = useState('');
    const [numReview, setNumReview] = useState('');

    const ProductSave = useSelector(state => state.productSave);
    const { loading: loadingSave, success: successSave, error: errorSave } = ProductSave;
    const dispatch = useDispatch();

    useEffect(() => {
      
              return () => {
                  //
          };
  
      }, []);

     const submitHandler = (e) => {
         e.preventDefault();
         dispatch(saveProduct(name, price, image, brand, category, countInStock, descriptions, rating, numReview));
     }
    return <div className="form">
        <form onSubmit= {submitHandler}>
            <ul className="form-container">
                <li>
                    <h2>Create Product</h2>
                </li>
                { loadingSave && <div>Loading...</div>}
                {errorSave && <div>{error}</div>}
                <li>
                    <label htmlFor="name">
                        Name
                    </label>
                    <input type="text" name="name" id="name" onChange={(e) => setName(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="name">
                        Price
                    </label>
                    <input type="text" name="price" id="name" onChange={(e) => setPrice(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="name">
                        Image
                    </label>
                    <input type="text" name="image" id="name" onChange={(e) => setImage(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="name">
                        Brand
                    </label>
                    <input type="text" name="brand" id="name" onChange={(e) => setBrand(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="name">
                        Category
                    </label>
                    <input type="text" name="category" id="name" onChange={(e) => setCategory(e.target.value)}></input>
                </li>
                
                <li>
                    <label htmlFor="name">
                        Rating
                    </label>
                    <input type="text" name="rating" id="name" onChange={(e) => setRating(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="name">
                        Num Reviews
                    </label>
                    <input type="text" name="numreview" id="name" onChange={(e) => setNumReview(e.target.value)}></input>
                </li>
                <li>
                    <label htmlFor="name">
                        Description
                    </label>
                    <textarea name="name" id="name" onChange={(e) => setDescription(e.target.value)}></textarea>
                </li>
                <li>
                    <button type="submit" className="button signin">Create</button>
                </li>
                <li>
                    New to amadone?
                </li>
                <li>
                    <Link to="/register" className="button secondary" >Create your amadone account</Link>
                </li>
            </ul>
        </form>
       
    </div>
        
}
export default ProductsScreen;
