import React from 'react';
import './App.css';
import data from './data';
import HomeScreen from '/Screen/HomeScreen';
import ProductScreen from '.Screen/ProductScreen';
import {BrowserRouter, Route} from 'react-router-dom';
function App(){

  const  openMenu = () => {
    document.querySelector(".siderbar").classList.add("open");
  }
  const closeMenu = () => {
    document.querySelector(".siderbar").classList.remove("open");
  }

  return (
    <BrowserRouter>
      <div className="grid-container">
              <header className="header">
                  <div className="brand">
                      <button onClick={openMenu}>
                          &#9776;
                      </button>
                      <a href="index.html">amadone</a>
                  </div>
                  <div className="header-links">
                      <a href="cart.html">Cart</a>
                      <a href="singin.html">Sign In</a>
                  </div>
              </header>
              <aside className="siderbar">
                  <h3>Shopping Categories</h3>
                  <button className="siderbar-close-button" onClick={closeMenu}>x</button>
                  <ul>
                      <li>
                          <a href="index.html">Pants</a>
                      </li>
                      <li>
                          <a href="index.html">Shirts</a>
                      </li>
                  </ul>
              </aside>

              <main className="main">
                  <div className="content">
                    <Route path="/products/:id" component={ProductScreen} />
                    <Route path="/" exact={true} component={HomeScreen} />
                      <ul className="products">
                        {
                          data.product.map(product => 
                            <li>
                            <div className="product">
                                <img className="product-image" src={product.image} alt="product"></img>
                                <div className="product-name">
                                    <a href="product.html">{product.name}</a>
                                </div>
                                <div className="product-brand">{product.brand}</div>
                                <div className="product-price">${product.price}</div>
                                <div className="product-rating">{product.rating} Stars ({product.numReviews} Review)</div>
                            </div>
                        </li>
                            )
                        }



                      </ul>
                  </div>

              </main>
              <footer className="footer">
                  <i> All right nDs</i>
              </footer>
          </div>
        </BrowserRouter>
  );
}

export default App;
